class TreeNode:
    def __init__(self, val=0, left=None, right=None):
        self.val = val
        self.left = left
        self.right = right

def search_tree(root, target):
    if not root or root.val == target:
        return root
    if target < root.val:
        return search_tree(root.left, target)
    return search_tree(root.right, target)
